import {
  Modal,
  ModalOverlay,
  ModalContent,
  ModalHeader,
  ModalFooter,
  ModalBody,
  ModalCloseButton,
  useDisclosure,
  Button,
  FormControl,
  FormLabel,
  Input,
} from '@chakra-ui/react'
import React, { useState } from 'react'
import DropboxChooser from 'react-dropbox-chooser'

const CreateModel = ({children}) => {
  const { isOpen, onOpen, onClose } = useDisclosure()
  const [name,setname] = useState()
  const [age,setage] = useState()
  const [gender,setgender] = useState()
  const [pid,setpid] = useState()
  const [file1,setfile1] = useState()
  const [file2,setfile2] = useState()
  const [status,setstatus] = useState()


  const APP_KEY = 'xkrb8rbksq7pgom'

  const  handleSuccess = (files) =>{
    console.log(files[0].link.toString())
    setfile1(files[0].link)
    
  }
  const  handleSuccess2 = (files) =>{
    console.log(files)
    setfile2(files[0].link.toString())
    
  }
  const handlesubmit = async() =>{
    setstatus("pending")
    setpid("DE-67429")
    if(!name){
      setname("-")
    }
    if(!age){
      setage("-")
    }
    if(!gender){
      setgender("-")
    }

    try{
      const config = {
        headers: {
          "Content-type": "application/json",
        },
      };
      const { data } = await axios.post(
        "/api/user",
        {
          name,
          age,
          gender,
          status,
          file1,
          f
        },
        config
      );

    } catch{

    }
    

  }



  return (
    <>
      <span onClick={onOpen}>{children}</span>

      <Modal isOpen={isOpen} onClose={onClose}>
        <ModalOverlay />
        <ModalContent>
          <ModalHeader>New Application</ModalHeader>
          <ModalCloseButton />
          <ModalBody>
            <FormControl>
              <FormLabel>Name</FormLabel>
              <Input
                placeholder="name"
                onChange={(e) => setname(e.target.value)}
              />
            </FormControl>

            <FormControl mt={4}>
              <FormLabel>age</FormLabel>
              <Input
                placeholder="age"
                onChange={(e) => setage(e.target.value)}
              />
            </FormControl>
            <FormControl mt={4}>
              <FormLabel>gender</FormLabel>
              <Input
                placeholder="gender"
                onChange={(e) => setgender(e.target.value)}
              />
            </FormControl>

            <FormControl id="pic">
              <FormLabel>Upload your 1st File</FormLabel>
              <DropboxChooser
                appKey={APP_KEY}
                success={handleSuccess}
                cancel={() => console.log("closed")}
                multiselect={true}
              >
                <Button>Choose a File </Button>
              </DropboxChooser>
            </FormControl>
            <FormControl id="pic">
              <FormLabel>Upload your 2nd File</FormLabel>
              <DropboxChooser
                appKey={APP_KEY}
                success={handleSuccess2}
                cancel={() => console.log("closed")}
                multiselect={true}
              >
                <Button>Choose a File</Button>
              </DropboxChooser>
            </FormControl>
          </ModalBody>

          <ModalFooter>
            <Button colorScheme="blue" mr={3} onClick={onClose}>
              Close
            </Button>
            <Button variant="ghost" onClick={handlesubmit}>Submit</Button>
          </ModalFooter>
        </ModalContent>
      </Modal>
    </>
  );
}

export default CreateModel
